                languageId: languageIdEnglish,
                toneId: toneIdConvincing,
                useCaseId,
                inputContexts,
                variations: 1,
                userId: "USER1",
                format: "html",
            },
        }).then((res)=>{console.log('final res',res);
            rootStore.pageStore.setMainPageContent(res.data.data[0]);
            return res.data.data[0].text;
        }).catch((e)=>{
            console.log('final error',e);
        });
    } catch (error) {
        console.log(error);
    }
}
